console.log('ðŸ§ª pULID JavaScript Implementation Test Runner');
console.log('='.repeat(50));

// Test 1: Basic functionality test
console.log('\n1. Testing basic pULID functionality...');
const testResults = test();
if (testResults.overall) {
  console.log('âœ… Basic functionality test PASSED');
} else {
  console.log('âŒ Basic functionality test FAILED');
  console.log('Details:', testResults);
  process.exit(1);
}

// Test 2: Parse golang-generated pULIDs
console.log('\n2. Testing golang compatibility...');
let parseErrors = 0;
let conversionErrors = 0;
let scopeErrors = 0;

golangTestData.forEach(({ entity, ulid, uuid, scope }) => {
  try {
    // Parse ULID
    const parsed = parse(ulid);
    
    // Check scope
    if (parsed.getScope() !== scope) {
      console.log(`âŒ Scope mismatch for ${entity}: expected ${scope}, got ${parsed.getScope()}`);
      scopeErrors++;
    }
    
    // Check UUID conversion
    const convertedUuid = parsed.toUUID();
    if (convertedUuid !== uuid) {
      console.log(`âŒ UUID conversion mismatch for ${entity}`);
      console.log(`   Expected: ${uuid}`);
      console.log(`   Got:      ${convertedUuid}`);
      conversionErrors++;
    }
    
    // Test round-trip
    const fromUuid = pULID.fromUUID(uuid);
    if (fromUuid.toString() !== ulid) {
      console.log(`âŒ Round-trip conversion failed for ${entity}`);
      conversionErrors++;
    }
    
  } catch (error) {
    console.log(`âŒ Parse error for ${entity}: ${error.message}`);
    parseErrors++;
  }
});

if (parseErrors === 0 && conversionErrors === 0 && scopeErrors === 0) {
  console.log('âœ… Golang compatibility test PASSED');
} else {
  console.log(`âŒ Golang compatibility test FAILED`);
  console.log(`   Parse errors: ${parseErrors}`);
  console.log(`   Conversion errors: ${conversionErrors}`);
  console.log(`   Scope errors: ${scopeErrors}`);
  process.exit(1);
}

// Test 3: Generate new pULIDs with correct scopes
console.log('\n3. Testing entity-specific generation...');
let generationErrors = 0;

golangTestData.forEach(({ entity, ulid, uuid, scope }) => {
  try {
    const parsed = parse(ulid);
    
    if (parsed.getScope() !== scope) {
      console.log(`âŒ Generated pULID for ${entity} has wrong scope: expected ${scope}, got ${parsed.getScope()}`);
      generationErrors++;
    }
  } catch (error) {
    console.log(`âŒ Generation error for ${entity}: ${error.message}`);
    generationErrors++;
  }
});

if (generationErrors === 0) {
  console.log('âœ… Entity-specific generation test PASSED');
} else {
  console.log(`âŒ Entity-specific generation test FAILED (${generationErrors} errors)`);
  process.exit(1);
}

